nta.declaration:
// Place global declarations here.
int  CPU1_C;
chan CPU1_use, CPU1_done;
int  CPU2_C;
chan CPU2_use, CPU2_done;

// CompTime of each job for each CPU
int JOBA_C1 = 1;
int JOBA_C2 = 2;
